mapper.select:

	SELECT MEM_ID
	  FROM TB_MEMBER
	 WHERE DI_CERTI = #{di_certi}
   	   AND POINT_CODE = #{point_code}


	SELECT COUNT(*) AS CNT
	  FROM TB_MEMBER
	 WHERE MEM_ID = #{mem_id}
   	   AND POINT_CODE = #{point_code}

mapper.update:

 	UPDATE TB_MEMBER SET MEM_PW = xx1.enc_varchar2_ins(#{mem_pw}, 10, 'PWD')
				   WHERE DI_CERTI = #{di_certi}
				     AND POINT_CODE = #{point_code}

	UPDATE TB_MEMBER SET HOME_ADDR1 = xx1.enc_varchar2_ins(UTL_RAW.CAST_TO_VARCHAR2(#{home_addr1}), 10, 'ADDRESS')
	                    ,HOME_ADDR2 = xx1.enc_varchar2_ins(UTL_RAW.CAST_TO_VARCHAR2(#{home_addr2}), 10, 'ADDRESS')
	                    ,MOBILE_NUM = xx1.enc_varchar2_ins(#{mobile_num}, 10, 'PHONE')
	                    ,UPD_ID = #{upd_id}
	                    ,UPD_DATE = sysdate
	                    ,RECEIVED_INFO_AT = #{received_info_at}

                        ,MEM_ID = #{mem_id}

				   WHERE MEM_UID = #{mem_uid}

UPDATE TB_MEMBER SET UPD_PW_DATE = SYSDATE
                    ,UPD_ID = #{upd_id}

                    ,MEM_PW = xx1.enc_varchar2_ins(#{mem_pw}, 10, 'PWD')

                    ,UPD_DATE = SYSDATE
			   WHERE MEM_UID = #{mem_uid}

MERGE INTO TB_NICE_CHECK n
USING DUAL
ON(n.MEM_NM = #{NAME} AND n.SUB_DI_CERTI = #{DI_CERTI})
	WHEN MATCHED THEN
    	UPDATE SET n.DI_CERTI = #{DI_CERTI}, n.UPD_DATE = SYSDATE
	WHEN NOT MATCHED THEN
	INSERT(
	   	   n.MEM_NM
	     , n.MOBILE_NUM
	     , n.DI_CERTI
	     , n.SUB_DI_CERTI
	     , n.INS_DATE
	) VALUES (
	       #{NAME}
	     , #{MOBILE_NUM}
	     , #{DI_CERTI}
	     , #{DI_CERTI}
	     , SYSDATE
	)

mapper.select:

	SELECT COUNT(*) AS CNT
	    FROM TB_NICE_CHECK
	WHERE MEM_NM = #{mem_nm}
   	    AND SUB_DI_CERTI = #{ci_certi}
   	    AND MOBILE_NUM = #{mobile_num}
