mapper.select:
SELECT COUNT(*)
  FROM TB_DESK_ADMIN_AUTH AA
 WHERE AA.ADMIN_UID=#{adminNum}
   AND AA.MENU_CODE=#{sMenuCode}

mapper.insert:
INSERT INTO TB_DESK_ADMIN_AUTH(
        AUTH_UID
       ,ADMIN_UID
       ,MENU_CODE
       ,INS_IP
       ,INS_ADMIN_ID
       ,INS_DATE
) VALUES (
        (SELECT NVL(MAX(AUTH_UID),0) + 1 FROM TB_DESK_ADMIN_AUTH)
       ,#{ADMIN_UID}
       ,#{MENU_CODE}
       ,#{INS_IP}
       ,#{INS_ADMIN_ID}
       ,SYSDATE
)

mapper.update:
UPDATE TB_DESK_ADMIN_AUTH SET
         UPD_IP=#{UPD_IP}
        ,UPD_IP=#{UPD_IP}
        ,UPD_DATE=SYSDATE
        ,ADMIN_UID=#{ADMIN_UID}
        ,MENU_CODE=#{MENU_CODE}
WHERE AUTH_UID=#{num}
