mapper.select:
SELECT ADMIN_UID ,MEMBER_VIEW_AT ,ADMIN_NM ,ADMIN_ID ,POINT_CODE ,ADMIN_DEPT ,ADMIN_PHONE ,USE_AT ,LOGIN_CNT ,LOGIN_DATE ,LOGIN_DIGIT ,INS_IP ,INS_ADMIN_ID ,INS_DATE ,UPD_IP ,UPD_ADMIN_ID ,UPD_DATE ,CERTNUM FROM TB_ADMIN_INFO WHERE ADMIN_ID=#{admin_id}
AND ADMIN_PW=STR_TO_ROW(#{admin_pw})
AND REPLACE(ADMIN_PHONE,'-','')=REPLACE(#{hpnum},'-','')
AND CERTNUM=#{certnum}
AND USE_AT='Y'

mapper.update:
UPDATE TB_ADMIN_INFO SET
         CERTNUM=#{certnum}
WHERE ADMIN_ID=#{admin_id}

UPDATE TB_ADMIN_INFO SET
         LOGIN_CNT=LOGIN_CNT + 1
        ,LOGIN_DATE=SYSDATE
        ,LOGIN_DIGIT=#{login_digit}
WHERE ADMIN_ID=#{admin_id}
