mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT * FROM ( SELECT MEM_UID ,ROW_TO_STR(MEM_NM) as MEM_NM ,MEM_ID ,ROW_TO_STR(MOBILE_NUM) as MOBILE_NUM ,MEM_BIRTH ,MEM_GENDER ,INS_DATE ,(SELECT COUNT(1) FROM TB_RESERVATION WHERE MEM_UID = MEM_UID) AS RESERVE_CNT FROM TB_MEMBER WHERE 1=1
) order by
RESERVE_CNT ASCDESC ,
MEM_UID DESC ) TB ) WHERE rn BETWEEN #{pageListSize} * (#{page} - 1) + 1 AND #{pageListSize} * #{page}

mapper.update:
UPDATE TB_MEMBER SET
MEM_ID = #{mem_id}
,UPD_ID = #{up_id}
,UPD_DATE = SYSDATE
WHERE MEM_UID = #{mem_uid}
