AND POINT_CODE = #{pointCode}
AND (TITLE LIKE '%' || #{sw} || '%')
AND VIEW_START_DATE <= TO_CHAR(sysdate,'yyyy.mm.dd') AND (VIEW_END_DATE is null OR TO_CHAR(sysdate,'yyyy.mm.dd') <= VIEW_END_DATE)

mapper.insert:
	 	INSERT INTO TB_NEWS(
             NEWS_UID
           , POINT_CODE
           , KIND
           , HIT
           , VIEW_START_DATE
           , VIEW_END_DATE
           , START_DATE
           , END_DATE
           , WRITE_TYPE
           , TITLE
           , FILE_LIST
           , FILE_LIST_FULL
           , FILE_CONTENT
           , FILE_CONTENT_FULL
           , CONTENTS
           , INST_IP
           , INST_ID
           , INST_DATE
	 	) VALUES (
             #{NEWS_UID}
           , #{POINT_CODE}
           , #{KIND}
           , 0
           , #{VIEW_START_DATE}
           , #{VIEW_END_DATE}
           , #{START_DATE}
           , #{END_DATE}
           , #{WRITE_TYPE}
           , UTL_RAW.CAST_TO_VARCHAR2(#{TITLE_HEX})
           , UTL_RAW.CAST_TO_VARCHAR2(#{FILE_LIST_HEX})
           , #{FILE_LIST_FULL}
           , UTL_RAW.CAST_TO_VARCHAR2(#{FILE_CONTENT_HEX})
           , #{FILE_CONTENT_FULL}
           , UTL_RAW.CAST_TO_VARCHAR2(#{CONTENTS_HEX})
           , #{INST_IP}
           , #{INST_ID}
           , SYSDATE
	 	)

mapper.update:
		UPDATE TB_NEWS SET
              UPDT_IP=#{UPDT_IP}
            , UPDT_ID=#{UPDT_ID}
            , UPDT_DATE=SYSDATE
            ,POINT_CODE=#{POINT_CODE}
            ,KIND=#{KIND}
            ,VIEW_START_DATE=#{VIEW_START_DATE}
            ,VIEW_END_DATE=#{VIEW_END_DATE}
            ,START_DATE=#{START_DATE}
            ,END_DATE=#{END_DATE}
            ,WRITE_TYPE=#{WRITE_TYPE}
            ,TITLE=UTL_RAW.CAST_TO_VARCHAR2(#{TITLE_HEX})
            ,FILE_LIST=UTL_RAW.CAST_TO_VARCHAR2(#{FILE_LIST_HEX}) ,FILE_LIST_FULL=#{FILE_LIST_FULL}
            ,FILE_CONTENT=UTL_RAW.CAST_TO_VARCHAR2(#{FILE_CONTENT_HEX}) ,FILE_CONTENT_FULL=#{FILE_CONTENT_FULL}
            ,CONTENTS=UTL_RAW.CAST_TO_VARCHAR2(#{CONTENTS_HEX})
		WHERE NEWS_UID=#{num}

		UPDATE TB_NEWS SET
              HIT = HIT + 1
		WHERE NEWS_UID=#{num}
